import cron from "node-cron";
import { readFolder } from "./logManager.js";

export const task = async (size) => {
  cron.schedule("0 0 */10 * *", async () => {
    try {
      console.log("Running log cleanup.");
      await readFolder(size);
    } catch (error) {
      console.log(error);
    }
  });
};
